<?phpheader('Content-Type: text/plain; charset=UTF-8');//application/jsoninclude 'savePhrase.php';
$err="Все ок";if(empty($_POST['strRus'])) $err="Русский текст не заполнен";if(empty($_POST['strLang'])) $err="Иностранный текст не заполнен";if(empty($_POST['lang'])) $err="Не задан язык";if(empty($_POST['file'])) $err="Не задано имя файла";$logfile="log.txt";$fileDescr = fopen($logfile, "w");fwrite($fileDescr, "Все идет нормально");fclose($fileDescr);$writeResultRus = savePhrase($_POST['strRus'],"ru", $_POST['file']."Rus");$writeResult = savePhrase($_POST['strLang'],$_POST['lang'], $_POST['file']);if(!$writeResult or !$writeResultRus) {	$err="Не удалось создать файл. Проверьте корректность имени файла.";	$output .= '{"fileNameRus":"';	$output .= ' ';	$output .= '","fileNameLang":"';	$output .= ' ';	$output .= '","errorText":"';	$output .= $err;	$output .= '"}';				}else{	$output .= '{"fileNameRus":"';	$output .= $_POST['file'].'Rus';	$output .= '","fileNameLang":"';	$output .= $_POST['file'];	$output .= '","errorText":"';	$output .= $err;	$output .= '"}';	}		echo $output; 
